using System.Collections; using System.Collections.Generic; using UnityEngine;  namespace sakana {     public class ObjectManager : MonoBehaviour     {         GameObject Effect1Obj;         GameObject Effect2Obj;         GameObject Effect3Obj;         Renderer[] mr = new Renderer[3];           GameObject manager;         ThresholdManager threshold;           int[] InstatiateCount = new int[3];          void Awake()         {              manager = GameObject.Find("Threshold");             threshold = manager.GetComponent<ThresholdManager>();         }         void Start()         {              Effect1Obj = GameObject.Find("First") as GameObject;             Effect2Obj = GameObject.Find("Second") as GameObject;             Effect3Obj = GameObject.Find("Third") as GameObject;              Effect1Obj.SetActive(false);             Effect2Obj.SetActive(false);             Effect3Obj.SetActive(false);         }           void Update()         {             if(threshold.mode == 0){                 Effect1Obj.SetActive(true);                 Effect2Obj.SetActive(false);                 Effect3Obj.SetActive(false);             }else if(threshold.mode == 1){                 Effect1Obj.SetActive(false);                 Effect2Obj.SetActive(true);                 Effect3Obj.SetActive(false);             }else if(threshold.mode == 2){                 Effect1Obj.SetActive(false);                 Effect2Obj.SetActive(false);                 Effect3Obj.SetActive(true);             }         }     } } 